sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (!isLoading) {
      //If the new value isn't blank
		var check = g_form.getDisplayBox('service_apps').value;

		resetForms();
		setUserID();
		optionsReadFalse();



		if (check=='BiSaver') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='BTPP') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='CCM') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='CCN') {

			g_form.removeOption('u_choice_3','cert');
			g_form.addOption('u_choice_3','test','TEST');

        }
		else if (check=='CharlesRivers') {

			g_form.addOption('u_choice_3','dev','DEV');
        }
		else if (check=='CLS') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='DAR') {

			g_form.removeOption('u_choice_3','cert');		

        }
		else if (check=='Dealer Track') {

			g_form.removeOption('u_choice_3','cert');
			g_form.addOption('u_choice_3','qatest','QA(test)');

        }
		else if (check=='Director') {

			g_form.removeOption('u_choice_3','cert');
			g_form.removeOption('u_choice_3','prod');
			g_form.addOption('u_choice_3','prod738','PROD738');
			g_form.addOption('u_choice_3','prod262','PROD262');
			g_form.addOption('u_choice_3','p2738','P2 738');
			g_form.addOption('u_choice_3','p7738','P7 738');
			g_form.addOption('u_choice_3','p2262','P2 262');
			g_form.addOption('u_choice_3','p7262','P7 262');

        }
		else if (check=='ECM') {

			g_form.removeOption('u_choice_3','cert');		

        }
		else if (check=='eOscar') {

			g_form.removeOption('u_choice_3','cert');
			g_form.setDisplay('appreg', true);

        }
		else if (check=='Equifax') {

			g_form.removeOption('u_choice_3','cert');
			g_form.setDisplay('company', true);

        }
		else if (check=='Experian') {

			g_form.removeOption('u_choice_3','cert');		

        }
		else if (check=='FannieMae') {

			g_form.removeOption('u_choice_3','cert');

		}
		else if (check=='FHA Connection') {

			g_form.removeOption('u_choice_3','cert');
        }
		else if (check=='GiftWrap') {

			g_form.removeOption('u_choice_3','cert');	
        }
		else if (check=='Globe Tax') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='iGrafx') {

			g_form.addOption('u_choice_3','dev','DEV');

			g_form.showFieldMsg('user1','Note: This options is for local IDs only. All other access is AD Group driven and based on your LAN password.','info');

        }
		else if (check=='LKE') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='LLB') {

			g_form.removeOption('u_choice_3','cert');
			g_form.addOption('u_choice_3','test','TEST');

        }
		else if (check=='LS Process Management') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='MERS') {

			g_form.removeOption('u_choice_3','cert');
			g_form.addOption('u_choice_3','test','TEST');

        }
		else if (check=='Paragon-IPS') {

			g_form.addOption('u_choice_3','dev','DEV');

        }
		else if (check=='Pay3000') {

			unSetUserID();

			g_form.setDisplay('pay3k', true);
			g_form.setDisplay('tsoid', true);
			g_form.setMandatory('tsoid', true);
			g_form.setMandatory('pay3k', true);

        }
		else if (check=='Powercell') {

			g_form.addOption('u_choice_3','dev','DEV');
			g_form.setDisplay('admin1', true);

        }
		else if (check=='Route One') {

			g_form.removeOption('u_choice_3','cert');
			g_form.addOption('u_choice_3','qatest','QA(test)');

        }
		else if (check=='Spectrum-XFS') {

			g_form.addOption('u_choice_3','dev','DEV');

        }
		else if (check=='STRATA') {

			g_form.addOption('u_choice_3','dev','DEV');

        }
		else if (check=='TaxPort') {


        }
		else if (check=='TransUnion') {

			g_form.removeOption('u_choice_3','cert');

        }
		else if (check=='WebDirect') {

			g_form.removeOption('u_choice_3','cert');
			g_form.addOption('u_choice_3','test','TEST');

        }
		else if (check=='WI') {

        }


	}   
}	
function optionsReadFalse(){

	g_form.setReadOnly('u_boolean_5', true);
	g_form.setReadOnly('u_boolean_6', true);
	g_form.setReadOnly('u_boolean_7', true);
	g_form.setValue('u_boolean_5', false);
	g_form.setValue('u_boolean_6', false);
	g_form.setValue('u_boolean_7', false);
	g_form.showFieldMsg('req_type','These are the set options for this application. They cannot be altered.','warning');
}	
function setUserID(){

	g_form.setDisplay('user1', true);
	g_form.setMandatory('user1', true);

}
function unSetUserID(){


	g_form.setMandatory('user1', false);
	g_form.setDisplay('user1', false);

}
function resetForms(){

	g_form.removeOption('u_choice_3','dev');
	g_form.removeOption('u_choice_3','qatest');
	g_form.removeOption('u_choice_3','test');
	g_form.addOption('u_choice_3','cert','CERT');
	g_form.addOption('u_choice_3','prod','PROD');

	g_form.setDisplay('company', false);
	g_form.setDisplay('appreg', false);

	g_form.setDisplay('pay3k', false);
	g_form.setDisplay('tsoid', false);

	g_form.removeOption('u_choice_3','prod738');
	g_form.removeOption('u_choice_3','prod262');
	g_form.removeOption('u_choice_3','p2738');
	g_form.removeOption('u_choice_3','p7738');
	g_form.removeOption('u_choice_3','p2262');
	g_form.removeOption('u_choice_3','p7262');

	g_form.setDisplay('field1', false);
	g_form.setDisplay('field2', false);
	g_form.setDisplay('field3', false);
	g_form.setDisplay('field4', false);

	g_form.setDisplay('admin1', false);
	g_form.setDisplay('admin2', false);
	g_form.setDisplay('admin3', false);

	g_form.hideAllFieldMsgs();

	g_form.setMandatory('user1', false);
	g_form.setDisplay('user1', false);
	g_form.setMandatory('tsoid', false);
	g_form.setDisplay('tsoid', false);
	g_form.setMandatory('pay3k', false);
	g_form.setDisplay('pay3k', false);
}
